$(document).ready(function () {
    $('#Dati-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Dati-add').modal('show');
        $('#Dati-create_btn').unbind();
        $('#Dati-create_btn').on('click', function () {
            addDatiSave();
        });
    });
});

function addDatiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-Dati-Active').is(":checked"),
				idParent:$('#Add-Dati-IdParent').val(),
				kodeDati:$('#Add-Dati-KodeDati').val(),
				namaDati:$('#Add-Dati-NamaDati').val(),
				tipe:$('#Add-Dati-Tipe').val(),

            }
            RequestData('POST', `/v1/Dati/add`, '#md-Dati-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Dati-add').modal('hide');
                    getListDati();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
