function editDatiDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Dati-edit').modal('show');

	$('#Edit-Dati-Active').prop('checked', data.active);
	$('#Edit-Dati-IdParent').val(data.idParent);
	$('#Edit-Dati-KodeDati').val(data.kodeDati);
	$('#Edit-Dati-NamaDati').val(data.namaDati);
	$('#Edit-Dati-Tipe').val(data.tipe);


    $('#md-Dati-edit').data('id', data.id);
    $('#Dati-edit_btn').unbind();
    $('#Dati-edit_btn').on('click', function () {
        editDatiSave();
    });
}

function editDatiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-Dati-Active').is(":checked"),
				idParent:$('#Edit-Dati-IdParent').val(),
				kodeDati:$('#Edit-Dati-KodeDati').val(),
				namaDati:$('#Edit-Dati-NamaDati').val(),
				tipe:$('#Edit-Dati-Tipe').val(),

            }
            RequestData('PUT', `/v1/Dati/edit/${$('#md-Dati-edit').data('id')}`, '#md-Dati-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Dati-edit').modal('hide');
                    getListDati();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
