$(document).ready(function () {
    $('#DocumentRepository-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-DocumentRepository-add').modal('show');
        $('#DocumentRepository-create_btn').unbind();
        $('#DocumentRepository-create_btn').on('click', function () {
            addDocumentRepositorySave();
        });
    });
});

function addDocumentRepositorySave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				base64:$('#Add-DocumentRepository-Base64').val(),
				code:$('#Add-DocumentRepository-Code').val(),
				description:$('#Add-DocumentRepository-Description').val(),
				filename:$('#Add-DocumentRepository-Filename').val(),
				modul:$('#Add-DocumentRepository-Modul').val(),
				subject:$('#Add-DocumentRepository-Subject').val(),

            }
            RequestData('POST', `/v1/DocumentRepository/add`, '#md-DocumentRepository-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-DocumentRepository-add').modal('hide');
                    getListDocumentRepository();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
