function editDocumentRepositoryDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-DocumentRepository-edit').modal('show');

	$('#Edit-DocumentRepository-Base64').val(data.base64);
	$('#Edit-DocumentRepository-Code').val(data.code);
	$('#Edit-DocumentRepository-Description').val(data.description);
	$('#Edit-DocumentRepository-Filename').val(data.filename);
	$('#Edit-DocumentRepository-Modul').val(data.modul);
	$('#Edit-DocumentRepository-Subject').val(data.subject);


    $('#md-DocumentRepository-edit').data('id', data.id);
    $('#DocumentRepository-edit_btn').unbind();
    $('#DocumentRepository-edit_btn').on('click', function () {
        editDocumentRepositorySave();
    });
}

function editDocumentRepositorySave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				base64:$('#Edit-DocumentRepository-Base64').val(),
				code:$('#Edit-DocumentRepository-Code').val(),
				description:$('#Edit-DocumentRepository-Description').val(),
				filename:$('#Edit-DocumentRepository-Filename').val(),
				modul:$('#Edit-DocumentRepository-Modul').val(),
				subject:$('#Edit-DocumentRepository-Subject').val(),

            }
            RequestData('PUT', `/v1/DocumentRepository/edit/${$('#md-DocumentRepository-edit').data('id')}`, '#md-DocumentRepository-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-DocumentRepository-edit').modal('hide');
                    getListDocumentRepository();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
