$(document).ready(function () {
    $('#Instansi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Instansi-add').modal('show');
        $('#Instansi-create_btn').unbind();
        $('#Instansi-create_btn').on('click', function () {
            addInstansiSave();
        });
    });
});

function addInstansiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-Instansi-Active').is(":checked"),
				name:$('#Add-Instansi-Name').val(),

            }
            RequestData('POST', `/v1/Instansi/add`, '#md-Instansi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Instansi-add').modal('hide');
                    getListInstansi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
