$(document).ready(function () {
    $('#Penelitian-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Penelitian-add').modal('show');
        $('#Penelitian-create_btn').unbind();
        $('#Penelitian-create_btn').on('click', function () {
            addPenelitianSave();
        });
    });
});

function addPenelitianSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				flowPosition:$('#Add-Penelitian-FlowPosition').val(),
				flowStatus:$('#Add-Penelitian-FlowStatus').val(),
				idBidangPenelitian:$('#Add-Penelitian-IdBidangPenelitian').val(),
				idInstansi:$('#Add-Penelitian-IdInstansi').val(),
				idKeperluan:$('#Add-Penelitian-IdKeperluan').val(),
				jangkaWaktuEnd:$('#Add-Penelitian-JangkaWaktuEnd').val(),
				jangkaWaktuStart:$('#Add-Penelitian-JangkaWaktuStart').val(),
				judulPenelitian:$('#Add-Penelitian-JudulPenelitian').val(),
				judulProposal:$('#Add-Penelitian-JudulProposal').val(),
				namaLembaga:$('#Add-Penelitian-NamaLembaga').val(),
				nomorSuratPengantar:$('#Add-Penelitian-NomorSuratPengantar').val(),
				penanggungJawab:$('#Add-Penelitian-PenanggungJawab').val(),
				proposalBase64:$('#Add-Penelitian-ProposalBase64').val(),
				proposalFilename:$('#Add-Penelitian-ProposalFilename').val(),
				ringkasanPenelitian:$('#Add-Penelitian-RingkasanPenelitian').val(),
				status:$('#Add-Penelitian-Status').val(),
				statusDescription:$('#Add-Penelitian-StatusDescription').val(),
				statusPenelitian:$('#Add-Penelitian-StatusPenelitian').val(),
				suratPengantarBase64:$('#Add-Penelitian-SuratPengantarBase64').val(),
				suratPengantarFilename:$('#Add-Penelitian-SuratPengantarFilename').val(),
				tanggalSuratPengantar:$('#Add-Penelitian-TanggalSuratPengantar').val(),

            }
            RequestData('POST', `/v1/Penelitian/add`, '#md-Penelitian-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Penelitian-add').modal('hide');
                    getListPenelitian();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
