$(document).ready(function () {
    getListPenelitian();
});

function getListPenelitian(page) {
    page = page != undefined ? page : 1;
    var pageSize = $('#Penelitian-page_select').val();
    var element = {
        table: '#Penelitian-table',
        tbody: '#Penelitian-tbody',
        from: '#Penelitian-from_page',
        to: '#Penelitian-to_page',
        total: '#Penelitian-total',
        pagination: '#Penelitian-pagination',
        item_pagination: 'Penelitian-item'
    }
    var param = {
        sort: {
            field: "id",
            type: 0
        },
        start: page,
        length: pageSize
    }
    RequestData('POST', "/v1/Penelitian/list", element.table, element.tbody, JSON.stringify(param), function (data) {
        if (data.succeeded) {
            $(element.tbody).html('');
            $(element.pagination).html('');
            if (data.list.length > 0) {
                SetTableData(true, 12, element, {
                    page: page,
                    pageSize: pageSize,
                    count: data.count,
                    function_name: 'getListPenelitian'
                }, function (count) {
                    data.list.forEach(function (item) {
                        $(element.tbody).append(`
                            <tr>
                                <td class="text-nowrap text-center">${count}</td>
								<td class="text-nowrap">${item.flowPosition}</td>
								<td class="text-nowrap">${item.flowStatus}</td>
								<td class="text-nowrap">${item.idBidangPenelitian}</td>
								<td class="text-nowrap">${item.idInstansi}</td>
								<td class="text-nowrap">${item.idKeperluan}</td>
								<td class="text-nowrap">${item.jangkaWaktuEnd}</td>
								<td class="text-nowrap">${item.jangkaWaktuStart}</td>
								<td class="text-nowrap">${item.judulPenelitian}</td>
								<td class="text-nowrap">${item.judulProposal}</td>
								<td class="text-nowrap">${item.namaLembaga}</td>
								<td class="text-nowrap">${item.nomorSuratPengantar}</td>
								<td class="text-nowrap">${item.penanggungJawab}</td>
								<td class="text-nowrap">${item.proposalBase64}</td>
								<td class="text-nowrap">${item.proposalFilename}</td>
								<td class="text-nowrap">${item.ringkasanPenelitian}</td>
								<td class="text-nowrap">${item.status}</td>
								<td class="text-nowrap">${item.statusDescription}</td>
								<td class="text-nowrap">${item.statusPenelitian}</td>
								<td class="text-nowrap">${item.suratPengantarBase64}</td>
								<td class="text-nowrap">${item.suratPengantarFilename}</td>
								<td class="text-nowrap">${item.tanggalSuratPengantar}</td>

                                <td class="text-center">
                                    <div>
                                        <button type="button" data-toggle="dropdown" class="btn btn-sm btn-info dropdown-toggle" aria-hashpopup="true" aria-expanded="false">Aksi</button>
                                        <div class="dropdown-menu">
                                            <a href="#!" class="dropdown-item" onclick="detailPenelitianDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Detail</a>
                                            <a href="#!" class="dropdown-item" onclick="editPenelitianDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Edit</a>
                                            <a href="#!" class="dropdown-item" onclick="deletePenelitianDialog(${item.id})">Delete</a>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        `);
                        count++;
                    });
                });
            } else {
                settabledata(false, 12, element);
            }
        } else {
            ShowNotif(`${data.message} : ${data.description}`, "error");
        }
    })
}

