$(document).ready(function () {
    $('#PenelitianAnggota-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-PenelitianAnggota-add').modal('show');
        $('#PenelitianAnggota-create_btn').unbind();
        $('#PenelitianAnggota-create_btn').on('click', function () {
            addPenelitianAnggotaSave();
        });
    });
});

function addPenelitianAnggotaSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				birthDate:$('#Add-PenelitianAnggota-BirthDate').val(),
				birthPlace:$('#Add-PenelitianAnggota-BirthPlace').val(),
				fullname:$('#Add-PenelitianAnggota-Fullname').val(),
				idPenelitian:$('#Add-PenelitianAnggota-IdPenelitian').val(),
				nik:$('#Add-PenelitianAnggota-Nik').val(),
				nip:$('#Add-PenelitianAnggota-Nip').val(),
				profession:$('#Add-PenelitianAnggota-Profession').val(),
				sort:$('#Add-PenelitianAnggota-Sort').val(),

            }
            RequestData('POST', `/v1/PenelitianAnggota/add`, '#md-PenelitianAnggota-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-PenelitianAnggota-add').modal('hide');
                    getListPenelitianAnggota();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
