function editPenelitianAnggotaDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-PenelitianAnggota-edit').modal('show');

	$('#Edit-PenelitianAnggota-BirthDate').val(data.birthDate);
	$('#Edit-PenelitianAnggota-BirthPlace').val(data.birthPlace);
	$('#Edit-PenelitianAnggota-Fullname').val(data.fullname);
	$('#Edit-PenelitianAnggota-IdPenelitian').val(data.idPenelitian);
	$('#Edit-PenelitianAnggota-Nik').val(data.nik);
	$('#Edit-PenelitianAnggota-Nip').val(data.nip);
	$('#Edit-PenelitianAnggota-Profession').val(data.profession);
	$('#Edit-PenelitianAnggota-Sort').val(data.sort);


    $('#md-PenelitianAnggota-edit').data('id', data.id);
    $('#PenelitianAnggota-edit_btn').unbind();
    $('#PenelitianAnggota-edit_btn').on('click', function () {
        editPenelitianAnggotaSave();
    });
}

function editPenelitianAnggotaSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				birthDate:$('#Edit-PenelitianAnggota-BirthDate').val(),
				birthPlace:$('#Edit-PenelitianAnggota-BirthPlace').val(),
				fullname:$('#Edit-PenelitianAnggota-Fullname').val(),
				idPenelitian:$('#Edit-PenelitianAnggota-IdPenelitian').val(),
				nik:$('#Edit-PenelitianAnggota-Nik').val(),
				nip:$('#Edit-PenelitianAnggota-Nip').val(),
				profession:$('#Edit-PenelitianAnggota-Profession').val(),
				sort:$('#Edit-PenelitianAnggota-Sort').val(),

            }
            RequestData('PUT', `/v1/PenelitianAnggota/edit/${$('#md-PenelitianAnggota-edit').data('id')}`, '#md-PenelitianAnggota-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-PenelitianAnggota-edit').modal('hide');
                    getListPenelitianAnggota();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
