$(document).ready(function () {
    $('#PenelitianWorkflow-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-PenelitianWorkflow-add').modal('show');
        $('#PenelitianWorkflow-create_btn').unbind();
        $('#PenelitianWorkflow-create_btn').on('click', function () {
            addPenelitianWorkflowSave();
        });
    });
});

function addPenelitianWorkflowSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				callbackUrl:$('#Add-PenelitianWorkflow-CallbackUrl').val(),
				groupNo:$('#Add-PenelitianWorkflow-GroupNo').val(),
				idPenelitian:$('#Add-PenelitianWorkflow-IdPenelitian').val(),
				idUserRequester:$('#Add-PenelitianWorkflow-IdUserRequester').val(),
				navigationUrl:$('#Add-PenelitianWorkflow-NavigationUrl').val(),
				statusCode:$('#Add-PenelitianWorkflow-StatusCode').val(),
				statusDescription:$('#Add-PenelitianWorkflow-StatusDescription').val(),
				stepNo:$('#Add-PenelitianWorkflow-StepNo').val(),
				workflowCode:$('#Add-PenelitianWorkflow-WorkflowCode').val(),
				workflowName:$('#Add-PenelitianWorkflow-WorkflowName').val(),

            }
            RequestData('POST', `/v1/PenelitianWorkflow/add`, '#md-PenelitianWorkflow-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-PenelitianWorkflow-add').modal('hide');
                    getListPenelitianWorkflow();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
