function editPenelitianWorkflowDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-PenelitianWorkflow-edit').modal('show');

	$('#Edit-PenelitianWorkflow-CallbackUrl').val(data.callbackUrl);
	$('#Edit-PenelitianWorkflow-GroupNo').val(data.groupNo);
	$('#Edit-PenelitianWorkflow-IdPenelitian').val(data.idPenelitian);
	$('#Edit-PenelitianWorkflow-IdUserRequester').val(data.idUserRequester);
	$('#Edit-PenelitianWorkflow-NavigationUrl').val(data.navigationUrl);
	$('#Edit-PenelitianWorkflow-StatusCode').val(data.statusCode);
	$('#Edit-PenelitianWorkflow-StatusDescription').val(data.statusDescription);
	$('#Edit-PenelitianWorkflow-StepNo').val(data.stepNo);
	$('#Edit-PenelitianWorkflow-WorkflowCode').val(data.workflowCode);
	$('#Edit-PenelitianWorkflow-WorkflowName').val(data.workflowName);


    $('#md-PenelitianWorkflow-edit').data('id', data.id);
    $('#PenelitianWorkflow-edit_btn').unbind();
    $('#PenelitianWorkflow-edit_btn').on('click', function () {
        editPenelitianWorkflowSave();
    });
}

function editPenelitianWorkflowSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				callbackUrl:$('#Edit-PenelitianWorkflow-CallbackUrl').val(),
				groupNo:$('#Edit-PenelitianWorkflow-GroupNo').val(),
				idPenelitian:$('#Edit-PenelitianWorkflow-IdPenelitian').val(),
				idUserRequester:$('#Edit-PenelitianWorkflow-IdUserRequester').val(),
				navigationUrl:$('#Edit-PenelitianWorkflow-NavigationUrl').val(),
				statusCode:$('#Edit-PenelitianWorkflow-StatusCode').val(),
				statusDescription:$('#Edit-PenelitianWorkflow-StatusDescription').val(),
				stepNo:$('#Edit-PenelitianWorkflow-StepNo').val(),
				workflowCode:$('#Edit-PenelitianWorkflow-WorkflowCode').val(),
				workflowName:$('#Edit-PenelitianWorkflow-WorkflowName').val(),

            }
            RequestData('PUT', `/v1/PenelitianWorkflow/edit/${$('#md-PenelitianWorkflow-edit').data('id')}`, '#md-PenelitianWorkflow-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-PenelitianWorkflow-edit').modal('hide');
                    getListPenelitianWorkflow();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
