$(document).ready(function () {
    $('#PenelitianWorkflowDetail-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-PenelitianWorkflowDetail-add').modal('show');
        $('#PenelitianWorkflowDetail-create_btn').unbind();
        $('#PenelitianWorkflowDetail-create_btn').on('click', function () {
            addPenelitianWorkflowDetailSave();
        });
    });
});

function addPenelitianWorkflowDetailSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				groupNo:$('#Add-PenelitianWorkflowDetail-GroupNo').val(),
				idPenelitianWorkflow:$('#Add-PenelitianWorkflowDetail-IdPenelitianWorkflow').val(),
				idUser:$('#Add-PenelitianWorkflowDetail-IdUser').val(),
				isReviewer:$('#Add-PenelitianWorkflowDetail-IsReviewer').is(":checked"),
				stepName:$('#Add-PenelitianWorkflowDetail-StepName').val(),
				stepNo:$('#Add-PenelitianWorkflowDetail-StepNo').val(),

            }
            RequestData('POST', `/v1/PenelitianWorkflowDetail/add`, '#md-PenelitianWorkflowDetail-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-PenelitianWorkflowDetail-add').modal('hide');
                    getListPenelitianWorkflowDetail();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
