function editPenelitianWorkflowDetailDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-PenelitianWorkflowDetail-edit').modal('show');

	$('#Edit-PenelitianWorkflowDetail-GroupNo').val(data.groupNo);
	$('#Edit-PenelitianWorkflowDetail-IdPenelitianWorkflow').val(data.idPenelitianWorkflow);
	$('#Edit-PenelitianWorkflowDetail-IdUser').val(data.idUser);
	$('#Edit-PenelitianWorkflowDetail-IsReviewer').prop('checked', data.isReviewer);
	$('#Edit-PenelitianWorkflowDetail-StepName').val(data.stepName);
	$('#Edit-PenelitianWorkflowDetail-StepNo').val(data.stepNo);


    $('#md-PenelitianWorkflowDetail-edit').data('id', data.id);
    $('#PenelitianWorkflowDetail-edit_btn').unbind();
    $('#PenelitianWorkflowDetail-edit_btn').on('click', function () {
        editPenelitianWorkflowDetailSave();
    });
}

function editPenelitianWorkflowDetailSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				groupNo:$('#Edit-PenelitianWorkflowDetail-GroupNo').val(),
				idPenelitianWorkflow:$('#Edit-PenelitianWorkflowDetail-IdPenelitianWorkflow').val(),
				idUser:$('#Edit-PenelitianWorkflowDetail-IdUser').val(),
				isReviewer:$('#Edit-PenelitianWorkflowDetail-IsReviewer').is(":checked"),
				stepName:$('#Edit-PenelitianWorkflowDetail-StepName').val(),
				stepNo:$('#Edit-PenelitianWorkflowDetail-StepNo').val(),

            }
            RequestData('PUT', `/v1/PenelitianWorkflowDetail/edit/${$('#md-PenelitianWorkflowDetail-edit').data('id')}`, '#md-PenelitianWorkflowDetail-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-PenelitianWorkflowDetail-edit').modal('hide');
                    getListPenelitianWorkflowDetail();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
