$(document).ready(function () {
    $('#PenelitianWorkflowLog-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-PenelitianWorkflowLog-add').modal('show');
        $('#PenelitianWorkflowLog-create_btn').unbind();
        $('#PenelitianWorkflowLog-create_btn').on('click', function () {
            addPenelitianWorkflowLogSave();
        });
    });
});

function addPenelitianWorkflowLogSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				groupNo:$('#Add-PenelitianWorkflowLog-GroupNo').val(),
				idPenelitianWorkflow:$('#Add-PenelitianWorkflowLog-IdPenelitianWorkflow').val(),
				idUser:$('#Add-PenelitianWorkflowLog-IdUser').val(),
				idWorkflowStatus:$('#Add-PenelitianWorkflowLog-IdWorkflowStatus').val(),
				isReviewer:$('#Add-PenelitianWorkflowLog-IsReviewer').is(":checked"),
				notes:$('#Add-PenelitianWorkflowLog-Notes').val(),
				statusDescription:$('#Add-PenelitianWorkflowLog-StatusDescription').val(),
				stepName:$('#Add-PenelitianWorkflowLog-StepName').val(),
				stepNo:$('#Add-PenelitianWorkflowLog-StepNo').val(),

            }
            RequestData('POST', `/v1/PenelitianWorkflowLog/add`, '#md-PenelitianWorkflowLog-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-PenelitianWorkflowLog-add').modal('hide');
                    getListPenelitianWorkflowLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
