function editPenelitianWorkflowLogDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-PenelitianWorkflowLog-edit').modal('show');

	$('#Edit-PenelitianWorkflowLog-GroupNo').val(data.groupNo);
	$('#Edit-PenelitianWorkflowLog-IdPenelitianWorkflow').val(data.idPenelitianWorkflow);
	$('#Edit-PenelitianWorkflowLog-IdUser').val(data.idUser);
	$('#Edit-PenelitianWorkflowLog-IdWorkflowStatus').val(data.idWorkflowStatus);
	$('#Edit-PenelitianWorkflowLog-IsReviewer').prop('checked', data.isReviewer);
	$('#Edit-PenelitianWorkflowLog-Notes').val(data.notes);
	$('#Edit-PenelitianWorkflowLog-StatusDescription').val(data.statusDescription);
	$('#Edit-PenelitianWorkflowLog-StepName').val(data.stepName);
	$('#Edit-PenelitianWorkflowLog-StepNo').val(data.stepNo);


    $('#md-PenelitianWorkflowLog-edit').data('id', data.id);
    $('#PenelitianWorkflowLog-edit_btn').unbind();
    $('#PenelitianWorkflowLog-edit_btn').on('click', function () {
        editPenelitianWorkflowLogSave();
    });
}

function editPenelitianWorkflowLogSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				groupNo:$('#Edit-PenelitianWorkflowLog-GroupNo').val(),
				idPenelitianWorkflow:$('#Edit-PenelitianWorkflowLog-IdPenelitianWorkflow').val(),
				idUser:$('#Edit-PenelitianWorkflowLog-IdUser').val(),
				idWorkflowStatus:$('#Edit-PenelitianWorkflowLog-IdWorkflowStatus').val(),
				isReviewer:$('#Edit-PenelitianWorkflowLog-IsReviewer').is(":checked"),
				notes:$('#Edit-PenelitianWorkflowLog-Notes').val(),
				statusDescription:$('#Edit-PenelitianWorkflowLog-StatusDescription').val(),
				stepName:$('#Edit-PenelitianWorkflowLog-StepName').val(),
				stepNo:$('#Edit-PenelitianWorkflowLog-StepNo').val(),

            }
            RequestData('PUT', `/v1/PenelitianWorkflowLog/edit/${$('#md-PenelitianWorkflowLog-edit').data('id')}`, '#md-PenelitianWorkflowLog-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-PenelitianWorkflowLog-edit').modal('hide');
                    getListPenelitianWorkflowLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
