function editUserDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-User-edit').modal('show');

	$('#Edit-User-Active').prop('checked', data.active);
	$('#Edit-User-Fullname').val(data.fullname);
	$('#Edit-User-IdRole').val(data.idRole);
	$('#Edit-User-Mail').val(data.mail);
	$('#Edit-User-Password').val(data.password);
	$('#Edit-User-PhoneNumber').val(data.phoneNumber);
	$('#Edit-User-PhotoBase64').val(data.photoBase64);
	$('#Edit-User-Token').val(data.token);
	$('#Edit-User-Username').val(data.username);


    $('#md-User-edit').data('id', data.id);
    $('#User-edit_btn').unbind();
    $('#User-edit_btn').on('click', function () {
        editUserSave();
    });
}

function editUserSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-User-Active').is(":checked"),
				fullname:$('#Edit-User-Fullname').val(),
				idRole:$('#Edit-User-IdRole').val(),
				mail:$('#Edit-User-Mail').val(),
				password:$('#Edit-User-Password').val(),
				phoneNumber:$('#Edit-User-PhoneNumber').val(),
				photoBase64:$('#Edit-User-PhotoBase64').val(),
				token:$('#Edit-User-Token').val(),
				username:$('#Edit-User-Username').val(),

            }
            RequestData('PUT', `/v1/User/edit/${$('#md-User-edit').data('id')}`, '#md-User-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-User-edit').modal('hide');
                    getListUser();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
