$(document).ready(function () {
    $('#UserProfile-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-UserProfile-add').modal('show');
        $('#UserProfile-create_btn').unbind();
        $('#UserProfile-create_btn').on('click', function () {
            addUserProfileSave();
        });
    });
});

function addUserProfileSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				address:$('#Add-UserProfile-Address').val(),
				birthDate:$('#Add-UserProfile-BirthDate').val(),
				birthPlace:$('#Add-UserProfile-BirthPlace').val(),
				gender:$('#Add-UserProfile-Gender').val(),
				idDati:$('#Add-UserProfile-IdDati').val(),
				nationality:$('#Add-UserProfile-Nationality').val(),
				nik:$('#Add-UserProfile-Nik').val(),
				nip:$('#Add-UserProfile-Nip').val(),
				profession:$('#Add-UserProfile-Profession').val(),

            }
            RequestData('POST', `/v1/UserProfile/add`, '#md-UserProfile-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-UserProfile-add').modal('hide');
                    getListUserProfile();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
